INSERT INTO `help` VALUES ('103', '联系合作', '零库存零风险高回报单件代发--爆招&nbsp; 实力代理<br />\r\n&nbsp;我们的承诺----每个代理都能月薪上万<br />\r\n<br />\r\n&nbsp;&nbsp; 选择我们的优势：<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 1.品牌效应--古由卡的人气网络<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 2.厂家直销 千万库存 价格优势<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 3.真人模特&nbsp; 实物拍摄&nbsp; 杜绝假货<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 4.七天无理由退货-淘宝合作制定合作商<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 5.市场监控 统一零售价&nbsp; 良好的发展空间<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp; 您还在等什么？<br />\r\n<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 火热招收实体加盟商<br />\r\n<br />\r\n我们的经营理念 ：实体店和网店相结合的经营方式，使其更有竞争力!&nbsp; <br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <br />\r\n网店+实店=零压货=高收益&nbsp;&nbsp; 打破传统营销模式 营造高效收益<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 新时代的营销&nbsp; 1+1&gt;2？ 古由卡服饰 帮您实现<br />\r\n<br />\r\n=--------------------------------------------------------------------<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 代理商的等级制度<br />\r\n每个代理商都有晋升的机会哦！请大家努力接单，共同创造财富！<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 成为超级代理级别的条件！<br />\r\n代理商的日均订单在10单以上者，可以申请超级代理商级别！审核期为一个礼拜（7天），由代理商想客服申请！<br />\r\n<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 超级代理商的优惠条件<br />\r\n审核通过后，代理商可以享受每个商的代理价格（-1）的优惠条件！卖得越好，赚得越多哦，请代理商确保销量，因为客服会定期审核，若代理商的日均订单不超 过10单，者降级为普通代理商！<br />\r\n<br />\r\n--------------------------------------------------------------------<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 批发商的等级制度<br />\r\n每个批发商也都有晋升的机会哦！请批发客户努力销售，共创财富佳绩！<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 成为超级批发商级别的条件！<br />\r\n批发商每周平均进货在1万元以上者，可以申请超级批发商级别！审核期为一个月（4周），由批发商向客服申请！<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 超级批发商的优惠条件<br />\r\n审核通过后，批发商可以销售每个商品批价-1元的优惠条件！卖得越多，赚得越多哦！请批发商确保销售量，因为客服会定期审核，若批发商的每周平均进货量不 超过1万则降级为普通批发商！<br />\r\n<br />\r\n<br />\r\n<br />\r\n<br />\r\n服务承诺：自京东售后服务部收到返修品并确认属于质量故障开始计时，在100分钟内处理完您的一切售后问题！<br />\r\n1、本服务仅针对购物15日内出现故障的客户，以客户收到产品之日起计算。 <br />\r\n2、以京东售后服务部确认属于产品质量故障开始计时，产品质量故障以国家三包法等有关法律、法规为准。<br />\r\n3、100分钟是指在京东售后拆包检测人员工作时间每周一至周五，上午9：00至12：00，下午13：00至17：00，法定节假日、停电等无法正常处 理情况除外。 <br />\r\n<br />\r\n<br />\r\n<br />\r\n业务合作伙伴<br />\r\n<br />\r\nUROVO  依靠有价值的业务合作伙伴网络，为我们的客户提供用于识别、跟踪和管理供应链资产的目标解决方案。我们的合作伙伴、系统集成商、分销商、独立软件供应商和 联盟是我们业务的基石和未来的发展空间。<br />\r\n<br />\r\n&nbsp;<br />\r\n&nbsp;<br />\r\n经销商合作<br />\r\n双赢，是我们的目标，合作的目的是利用我们双方的资源，最终实现为我们共同的客户提供高价值解决方案。我们会根据合作伙伴的不同能力和承诺来确定合作伙 伴。我们也会为我们的合作伙伴所做的努力给予回报，依此共赢。&nbsp;&nbsp; <br />\r\n&nbsp;<br />\r\n联盟合作<br />\r\n共赢，是我们的目的。利用我们的资源，结合您的优势，共写新篇章。我们可通过多种合作模式，实现合作目的。&nbsp;&nbsp; <br />\r\n&nbsp;<br />\r\n&nbsp; <br />', '22');
INSERT INTO `help` VALUES ('104', '联系合作', '零库存零风险高回报单件代发--爆招&nbsp; 实力代理<br />\r\n&nbsp;我们的承诺----每个代理都能月薪上万<br />\r\n<br />\r\n&nbsp;&nbsp; 选择我们的优势：<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 1.品牌效应--古由卡的人气网络<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 2.厂家直销 千万库存 价格优势<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 3.真人模特&nbsp; 实物拍摄&nbsp; 杜绝假货<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 4.七天无理由退货-淘宝合作制定合作商<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 5.市场监控 统一零售价&nbsp; 良好的发展空间<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp; 您还在等什么？<br />\r\n<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 火热招收实体加盟商<br />\r\n<br />\r\n我们的经营理念 ：实体店和网店相结合的经营方式，使其更有竞争力!&nbsp; <br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <br />\r\n网店+实店=零压货=高收益&nbsp;&nbsp; 打破传统营销模式 营造高效收益<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 新时代的营销&nbsp; 1+1&gt;2？ 古由卡服饰 帮您实现<br />\r\n<br />\r\n=--------------------------------------------------------------------<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 代理商的等级制度<br />\r\n每个代理商都有晋升的机会哦！请大家努力接单，共同创造财富！<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 成为超级代理级别的条件！<br />\r\n代理商的日均订单在10单以上者，可以申请超级代理商级别！审核期为一个礼拜（7天），由代理商想客服申请！<br />\r\n<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 超级代理商的优惠条件<br />\r\n审核通过后，代理商可以享受每个商的代理价格（-1）的优惠条件！卖得越好，赚得越多哦，请代理商确保销量，因为客服会定期审核，若代理商的日均订单不超 过10单，者降级为普通代理商！<br />\r\n<br />\r\n--------------------------------------------------------------------<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 批发商的等级制度<br />\r\n每个批发商也都有晋升的机会哦！请批发客户努力销售，共创财富佳绩！<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 成为超级批发商级别的条件！<br />\r\n批发商每周平均进货在1万元以上者，可以申请超级批发商级别！审核期为一个月（4周），由批发商向客服申请！<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 超级批发商的优惠条件<br />\r\n审核通过后，批发商可以销售每个商品批价-1元的优惠条件！卖得越多，赚得越多哦！请批发商确保销售量，因为客服会定期审核，若批发商的每周平均进货量不 超过1万则降级为普通批发商！<br />\r\n<br />\r\n<br />\r\n<br />\r\n<br />\r\n服务承诺：自京东售后服务部收到返修品并确认属于质量故障开始计时，在100分钟内处理完您的一切售后问题！<br />\r\n1、本服务仅针对购物15日内出现故障的客户，以客户收到产品之日起计算。 <br />\r\n2、以京东售后服务部确认属于产品质量故障开始计时，产品质量故障以国家三包法等有关法律、法规为准。<br />\r\n3、100分钟是指在京东售后拆包检测人员工作时间每周一至周五，上午9：00至12：00，下午13：00至17：00，法定节假日、停电等无法正常处 理情况除外。 <br />\r\n<br />\r\n<br />\r\n<br />\r\n业务合作伙伴<br />\r\n<br />\r\nUROVO  依靠有价值的业务合作伙伴网络，为我们的客户提供用于识别、跟踪和管理供应链资产的目标解决方案。我们的合作伙伴、系统集成商、分销商、独立软件供应商和 联盟是我们业务的基石和未来的发展空间。<br />\r\n<br />\r\n&nbsp;<br />\r\n&nbsp;<br />\r\n经销商合作<br />\r\n双赢，是我们的目标，合作的目的是利用我们双方的资源，最终实现为我们共同的客户提供高价值解决方案。我们会根据合作伙伴的不同能力和承诺来确定合作伙 伴。我们也会为我们的合作伙伴所做的努力给予回报，依此共赢。&nbsp;&nbsp; <br />\r\n&nbsp;<br />\r\n联盟合作<br />\r\n共赢，是我们的目的。利用我们的资源，结合您的优势，共写新篇章。我们可通过多种合作模式，实现合作目的。&nbsp;&nbsp; <br />\r\n&nbsp;<br />\r\n&nbsp; <br />', '22');
